settings.custom_liquid:
{% if product.metafields.custom.story_description != blank %}
<div class="story-section" style="background: linear-gradient(135deg, #D2BDC8, #F5F0F3); padding: 25px; border-radius: 12px; margin: 25px 0; border-left: 4px solid #87144F;">
  <h3 style="color: #87144F; margin-bottom: 15px; font-size: 1.2em;">üíù The Story Behind This Box</h3>
  <p style="color: #3D3633; font-style: italic; line-height: 1.6; font-size: 1.1em;">{{ product.metafields.custom.story_description }}</p>
</div>
{% endif %}

{% if product.metafields.custom.combo_contents != blank %}
<div class="combo-contents" style="border: 2px solid #87144F; padding: 20px; border-radius: 12px; margin: 25px 0; background: white;">
  <h3 style="color: #87144F; margin-bottom: 15px; text-align: center;">üì¶ What's Included in Your Box</h3>
  <div style="color: #3D3633; white-space: pre-line; line-height: 1.8;">{{ product.metafields.custom.combo_contents }}</div>
</div>
{% endif %}

{% if product.metafields.custom.delivery_promise != blank %}
<div class="delivery-promise" style="background: linear-gradient(135deg, #87144F, #A91B5B); color: white; padding: 15px; border-radius: 8px; text-align: center; margin: 20px 0; box-shadow: 0 4px 8px rgba(135, 20, 79, 0.3);">
  <strong style="font-size: 1.1em;">üöö {{ product.metafields.custom.delivery_promise }}</strong>
</div>
{% endif %}
